import{_ as e,l as a,k as t,ae as s}from"./chunks/framework.BFFW23Ds.js";const m=JSON.parse('{"title":"04 位移路程","description":"","frontmatter":{"cate":["运动的描述","时间 位移"],"type":"1,"},"headers":[{"level":2,"title":"位移（displacement）","slug":"位移-displacement","link":"#位移-displacement","children":[]},{"level":2,"title":"路程（distance）","slug":"路程-distance","link":"#路程-distance","children":[]},{"level":2,"title":"位移和路程区别","slug":"位移和路程区别","link":"#位移和路程区别","children":[]},{"level":2,"title":"一维位移表示方法","slug":"一维位移表示方法","link":"#一维位移表示方法","children":[]},{"level":2,"title":"根据坐标算位移","slug":"根据坐标算位移","link":"#根据坐标算位移","children":[]},{"level":2,"title":"路程位移比大小","slug":"路程位移比大小","link":"#路程位移比大小","children":[]}],"relativePath":"gaozhong/物理/合集/01 必修一/01 运动的描述/05 基础课：位移和路程.md","filePath":"gaozhong/物理/合集/01 必修一/01 运动的描述/05 基础课：位移和路程.md","lastUpdated":1718538062000}'),i={name:"gaozhong/物理/合集/01 必修一/01 运动的描述/05 基础课：位移和路程.md"},l=s('<h1 id="_04-位移路程" tabindex="-1">04 位移路程 <a class="header-anchor" href="#_04-位移路程" aria-label="Permalink to &quot;04 位移路程&quot;">​</a></h1><h2 id="位移-displacement" tabindex="-1">位移（displacement） <a class="header-anchor" href="#位移-displacement" aria-label="Permalink to &quot;位移（displacement）&quot;">​</a></h2><p>表示物体位置的变化的矢量。</p><p>大小：初末位置直线距离，方向：初位置指向末位置。</p><p>位移只看起点终点位置变化，不管中间运动过程</p><h2 id="路程-distance" tabindex="-1">路程（distance） <a class="header-anchor" href="#路程-distance" aria-label="Permalink to &quot;路程（distance）&quot;">​</a></h2><p>表示物体实际运动轨迹长度的标量。</p><p>大小：轨迹的长度。</p><h2 id="位移和路程区别" tabindex="-1">位移和路程区别 <a class="header-anchor" href="#位移和路程区别" aria-label="Permalink to &quot;位移和路程区别&quot;">​</a></h2><p>位移：描述位置的变化，与运动无关。</p><p>路程：与运动有关。</p><img src="http://my-course-public.oss-cn-shanghai.aliyuncs.com/public/assets/gaozhong_physics_3/physics_230.png"><h2 id="一维位移表示方法" tabindex="-1">一维位移表示方法 <a class="header-anchor" href="#一维位移表示方法" aria-label="Permalink to &quot;一维位移表示方法&quot;">​</a></h2><p><code>正负号+数字</code>，正负号表方向，数字表大小。</p><img src="http://my-course-public.oss-cn-shanghai.aliyuncs.com/public/assets/gaozhong_physics_3/physics_231.png"><h2 id="根据坐标算位移" tabindex="-1">根据坐标算位移 <a class="header-anchor" href="#根据坐标算位移" aria-label="Permalink to &quot;根据坐标算位移&quot;">​</a></h2><img src="http://my-course-public.oss-cn-shanghai.aliyuncs.com/public/assets/gaozhong_physics_3/physics_232.png"><p>0 到 1：</p><p>-2 到 2：</p><p>3 到 -2：</p><p><strong>位移=末位置坐标 - 初位置坐标</strong></p><h2 id="路程位移比大小" tabindex="-1">路程位移比大小 <a class="header-anchor" href="#路程位移比大小" aria-label="Permalink to &quot;路程位移比大小&quot;">​</a></h2><p>路程和位移不能比大小，位移是箭头（矢量），路程是数字（标量）。</p><p>路程和位移的大小可以比大小。</p>',24),n=[l];function c(o,h,r,p,d,_){return t(),a("div",null,n)}const g=e(i,[["render",c]]);export{m as __pageData,g as default};
