import{_ as a,l as e,k as l,ae as i}from"./chunks/framework.BFFW23Ds.js";const u=JSON.parse('{"title":"10 加减速判断","description":"","frontmatter":{"cate":["运动的描述","加速度"],"type":"1,"},"headers":[{"level":2,"title":"速度与什么有关","slug":"速度与什么有关","link":"#速度与什么有关","children":[]},{"level":2,"title":"a、v、 ∆𝑣 关系总结","slug":"a、v、-∆v-关系总结","link":"#a、v、-∆v-关系总结","children":[]},{"level":2,"title":"加减速判断","slug":"加减速判断","link":"#加减速判断","children":[]},{"level":2,"title":"加速度的增大减小","slug":"加速度的增大减小","link":"#加速度的增大减小","children":[]},{"level":2,"title":"核心三句话","slug":"核心三句话","link":"#核心三句话","children":[]},{"level":2,"title":"av 反向的运动全过程","slug":"av-反向的运动全过程","link":"#av-反向的运动全过程","children":[]},{"level":2,"title":"匀变速直线运动","slug":"匀变速直线运动","link":"#匀变速直线运动","children":[]}],"relativePath":"gaozhong/物理/合集/01 必修一/01 运动的描述/10 基础课：加减速判断.md","filePath":"gaozhong/物理/合集/01 必修一/01 运动的描述/10 基础课：加减速判断.md","lastUpdated":1720704808000}'),t={name:"gaozhong/物理/合集/01 必修一/01 运动的描述/10 基础课：加减速判断.md"},o=i('<h1 id="_10-加减速判断" tabindex="-1">10 加减速判断 <a class="header-anchor" href="#_10-加减速判断" aria-label="Permalink to &quot;10 加减速判断&quot;">​</a></h1><h2 id="速度与什么有关" tabindex="-1">速度与什么有关 <a class="header-anchor" href="#速度与什么有关" aria-label="Permalink to &quot;速度与什么有关&quot;">​</a></h2><img src="http://my-course-public.oss-cn-shanghai.aliyuncs.com/public/assets/gaozhong_physics_3/physics_242.png"><h2 id="a、v、-∆v-关系总结" tabindex="-1">a、v、 ∆𝑣 关系总结 <a class="header-anchor" href="#a、v、-∆v-关系总结" aria-label="Permalink to &quot;a、v、 ∆𝑣 关系总结&quot;">​</a></h2><p>v 与 a 有关，但是只靠 a 不能决定 v。</p><p>a 与 v、∆𝒗 均无关</p><p>∆𝒗 与 a 有关，a 可以决定 ∆𝒗 的方向，但不能决定大小</p><h2 id="加减速判断" tabindex="-1">加减速判断 <a class="header-anchor" href="#加减速判断" aria-label="Permalink to &quot;加减速判断&quot;">​</a></h2><p>av 同向必加速，av 反向必减速。</p><p>加减速判断：同加反减。</p><p>加减速判断必须同时看 av，单独靠 a 和 v 都无法判断！</p><h2 id="加速度的增大减小" tabindex="-1">加速度的增大减小 <a class="header-anchor" href="#加速度的增大减小" aria-label="Permalink to &quot;加速度的增大减小&quot;">​</a></h2><p>判断加减速只看 a 方向，不看 a 大小变化。</p><p>av 同向，即使 a 减小，速度也增加，只不过增加的越来越慢。</p><p>av 反向，即使 a 增加，速度也减小，只不过减小的越来越快。</p><h2 id="核心三句话" tabindex="-1">核心三句话 <a class="header-anchor" href="#核心三句话" aria-label="Permalink to &quot;核心三句话&quot;">​</a></h2><ol><li>a=0 物体未必静止，还可能做匀速直线运动</li><li>只知道 a 的增减，无法判断 v 的增减</li><li>只知道 a 的方向，无法判断 v 的方向</li></ol><h2 id="av-反向的运动全过程" tabindex="-1">av 反向的运动全过程 <a class="header-anchor" href="#av-反向的运动全过程" aria-label="Permalink to &quot;av 反向的运动全过程&quot;">​</a></h2><p>第一阶段：减速，减速到 0</p><p>第二阶段：从 0 反向加速，av 同向</p><h2 id="匀变速直线运动" tabindex="-1">匀变速直线运动 <a class="header-anchor" href="#匀变速直线运动" aria-label="Permalink to &quot;匀变速直线运动&quot;">​</a></h2><p>a 不变的直线运动</p><p>速度均匀变化</p><p>三种匀变速直线运动：</p><ol><li>加速</li><li>减速到 0 停止</li><li>减速到 0 然后反向加速</li></ol>',25),r=[o];function n(h,s,c,p,d,_){return l(),e("div",null,r)}const m=a(t,[["render",n]]);export{u as __pageData,m as default};
